mapper.select:
select categoryCode, categoryName
from IndustryCategory
where categoryCode = #{categoryCode}

		select categoryCode, categoryName, r
		from(
		select categoryCode, categoryName, rownum r
		from(
		select categoryCode, categoryName
		from IndustryCategory
		
			
				
					categoryCode like '%'||#{word}||'%'
				
				
					categoryName like '%'||#{word}||'%'
				
			
		
		ORDER BY categoryCode DESC

		)
		)                                                                            
                                                                           
   where r>=#{sno} and r<=#{eno}
