mapper.update:
    update offer
    set Title=#{title}, Qualification=#{qualification}, OfferDate=#{offerDate},
	Detail=#{offerDate}, EmploymentSector=#{employmentSector}, Task=#{task},
	WorkingArea=#{workingArea}, Recruitment=#{recruitment}, Salary=#{salary}
	where OfferNum = #{offerNum}

mapper.select:
	select OfferNum , Title, Qualification, OfferDate, CompanyName, recruitment, r
	from(
		select OfferNum , Title, Qualification, OfferDate, CompanyName, recruitment, rownum r
		from(
			select OfferNum , Title, Qualification, OfferDate, CompanyName, recruitment
			from Offer  
			
	
		
			
				title like '%'||#{word}||'%'
			
			
				CompanyName like '%'||#{word}||'%'
			
			
				WorkingArea like '%'||#{word}||'%'
			
		
	        	                                       

		ORDER BY OfferNum desc
		)
		)                                                                            
                                                                           
   where r>=#{sno} and r<=#{eno}

mapper.update:
	UPDATE offer
    SET viewCnt = viewCnt + 1
    WHERE offerNum = #{offerNum}
