mapper.update:
UPDATE OneByOne            
SET viewcnt = viewcnt + 1
WHERE oneByOneNum = #{oneByOneNum}

UPDATE OneByOne           
SET ansnum = ansnum + 1  
WHERE grpno=#{grpno} AND ansnum > #{ansnum}

UPDATE OneByOne                 
SET title=#{title}, content=#{content}  
WHERE oneByOneNum = #{oneByOneNum}
